function _sendFormRequest($data)
{
    $resData = [];
    error_log( date('r') . ' ' . __FUNCTION__ . ' +' . __LINE__ . ' $data: ' . PHP_EOL . var_export($data, 1) . PHP_EOL );
    $uri = 'https://nh.leadbox.com.ua/hubspot/074bmom3utuln3yym6joh12dnl6ow6lltiark1ar';
    $ch = curl_init();
    curl_setopt($ch, CURLOPT_URL, $uri);
    curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false); // On dev server only!
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
    curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
    curl_setopt($ch, CURLOPT_POST, 1);
    curl_setopt($ch, CURLOPT_POSTFIELDS, $data);
    $resCode = curl_errno($ch);
    $resData['code'] = $resCode;
    $result = curl_exec($ch);
    $resData['res'] = $result;
    // app()->getService('log')->logInfo(p('Request result: {0}', [var_export($result, 1)]));
    if ($result === false) {
        if (curl_error($ch)) {
            // app()->getService('log')->logInfo(p('Curl error: {0}', [var_export(curl_error($ch), 1)]));
            $resData['error'] = curl_error($ch);
        }
        if ($resCode !== 0) {
            // app()->getService('log')->logInfo(p('HTTP error code: {0}', [var_export($resCode, 1)]));
        }
    }
    return $resData;
}

if (!empty($_GET['email'])) {
    if (isset($_GET['category']) && !empty($_GET['category'])) {
        $sFormData = serialize(['email' => @$_GET['email'], 'category' => @$_GET['category'], 'mailingId' => @$_GET['id']]);
    } else {
        $sFormData = serialize(['email' => @$_GET['email'], 'mailingId' => @$_GET['id']]);
    }
    $formData = array_merge(['formData' => $sFormData], ['formName' => 'unsubscribe', 'formUri' => $_SERVER['REQUEST_URI']]);
    $res = _sendFormRequest($formData);
    error_log( date('r') . ' ' . __FILE__ . ' +' . __LINE__ . ' result: ' . PHP_EOL . var_export($res, 1) . PHP_EOL );
}

?>

